INSERT INTO `colleagues` (`id`, `name`, `photo`, `specialization`, `degree`, `listMethods`, `descriptionMethods`, `education`, `hobbies`, `titleAdditional`, `descriptionAdditional`, `certificates`, `notes`) VALUES
(1, 'Джулай Надежда Мироновна', '/photo/colleague/psiholog1.jpg', 'психиатр, психотерапевт', '', '- врачебная консультация, \\n\r\n- индивидуальная психотерапия (рациональная, психоанализ), \\n\r\n- групповая психотерапия, \\n\r\n- рефлексотерапия. \\n', '', 'В 2000г окончила Национальный медицинский университет по специальности «лечебное дело». \\n\r\nВ 2001г получила специализацию врача-невропатолога. Стаж работы неврологом 5 лет (ГКБ№4). \\n\r\nВ 2005г прошла специализацию по психиатрии и соответственно сменила профиль трудовой деятельности. \\n\r\nВ 2007г прошла специализацию по психотерапии и в 2008г начала работать в Центре психотерапии и мед психологии со стационаром пограничных состояний Киевской психоневрологической больницы им. И.П. Павлова. \\n\r\nВ 2009г прошла специализацию по рефлексотерапии. \\n\r\n2011 – 2016гг обучение техникам групповой психотерапии в «Школе развития психотерапии», в тч прохождение личного опыта по групповому психоанализу. \\n\r\nС 2018г присвоены Высшие категории по специальностям «Психиатрия» и «Психотерапия».\r\nВ настоящее время занимаюсь активной практической деятельностью (веду амбулаторный и стационарный приём, провожу сеансы психотерапии нескольких направлений), преподаванием. \\n', '- врачебная консультация\r\n- индивидуальная психотерапия (рациональная, психоанализ)\r\n- групповая психотерапия\r\n- рефлексотерапия\r\n- преподавание', '', '', '/photo/certificates/id1_1.jpg,/photo/certificates/id1_2.jpg,/photo/certificates/id1_3.jpg,/photo/certificates/id1_4.jpg,/photo/certificates/id1_5.jpg,/photo/certificates/id1_6.jpg', NULL),
(2, 'Демьяненко Елена Игоревна', '/photo/colleague/psiholog4.jpg', 'психолог', 'практикующий психолог высшей категории', '- ЛИЧНОСТНО-ОРИЕНТИРОВАННАЯ психотерапия,\\n\r\n- ПОВЕДЕНЧЕСКАЯ психотерапия,\\n\r\n- МОТИВАЦИОННО-ПОВЕДЕНЧЕСКАЯ психотерапия,\\n\r\n- РАЦИОНАЛЬНАЯ психотерапия,\\n\r\n- КОГНИТИВНАЯ психотерапия.\\n\r\n', '-  ЛИЧНОСТНО-ОРИЕНТИРОВАННОЙ психотерапии. Основной целью которой является достижение позитивных личностных изменений (коррекция нарушенной системы отношений, неадекватных когнитивных и поведенческих стереотипов), что ведет как к улучшению субъективного самочувствия пациента и устранению симптоматики, так и к восстановлению полноценного функционирования личности. \\n\r\n-  ПОВЕДЕНЧЕСКОЙ  психотерапии. Основной целью которой является коррекция неправильных, неадаптивных форм поведения и обучение новым, более эффективным и здоровым формам поведения. \\n\r\n-  МОТИВАЦИОННО-ПОВЕДЕНЧЕСКОЙ психотерапии. \\n\r\n-  РАЦИОНАЛЬНОЙ психотерапии. Основной целью которой является объяснение, убеждение, переориентация неправильных представлений, основанное на логической аргументации.\r\nКОГНИТИВНОЙ психотерапии. Основной целью которой является изменение дезадаптивных убеждений, коррекция когнитивных ошибок, изменение дисфункционального поведения. \\n', '1983-1988г - Университет им.Т.Г.Шевченко Специальность: \"Психология\". \\n\r\nС 1988г по настоящее время  - практический психолог Высшей категории Центра психотерапии и медицинской психологии Киевской городской клинической психоневрологической больницы №1. \\n\r\n1999г – повышение квалификации- Харьковская медицинская академия последипломного образования. Специализация «Психодиагностика и психокоррекция в клинике». \\n\r\n2009г – повышение квалификации – Национальная медицинская академия последипломного образования им. Шупика. Специализация “Пропедевтика психических заболеваний”. \\n\r\n2012г – предаттестационный цикл « Практическая психология» в институте последипломного  педагогического образования Киевского университета им. Б.Гринченко с присвоением квалификации «Специалист Первой категории». \\n\r\n2017г – Присвоена квалификационная категория «Специалист Высшей категории». \\n\r\n', '', 'В психокоррекционной работе использую техники:', '- ЛИЧНОСТНО-ОРИЕНТИРОВАННОЙ психотерапии. Основной целью которой является достижение позитивных личностных изменений (коррекция нарушенной системы отношений, неадекватных когнитивных и поведенческих стереотипов), что ведет как к улучшению субъективного самочувствия пациента и устранению симптоматики, так и к восстановлению полноценного функционирования личности.\\n\r\n- ПОВЕДЕНЧЕСКОЙ  психотерапии. Основной целью которой является коррекция неправильных, неадаптивных форм поведения и обучение новым, более эффективным и здоровым формам поведения.\\n\r\n- МОТИВАЦИОННО-ПОВЕДЕНЧЕСКОЙ психотерапии.\\n\r\n- РАЦИОНАЛЬНОЙ психотерапии. Основной целью которой является объяснение, убеждение, переориентация неправильных представлений, основанное на логической аргументации.\r\n- КОГНИТИВНОЙ психотерапии. Основной целью которой является изменение дезадаптивных убеждений, коррекция когнитивных ошибок, изменение дисфункционального поведения.\\n\r\n', '/photo/certificates/id4_1.jpg,/photo/certificates/id4_2.jpg,/photo/certificates/id4_3.jpg', NULL);

