INSERT INTO `offers` (`id`, `photo`, `title`, `content`, `price`, `duration`, `notes`) VALUES
(1, '/photo/offers/consultation.jpg', 'Врачебная консультация', 'Врачебная консультация – это собеседование с врачом-психотерапевтом, в результате которого обозначаются ведущие вопросы клиента и определяется дальнейшая тактика ведения: выбор вида психотерапии, назначение медикаментов или направление в стационар (при необходимости).', '1600', 'от 30 до 60 минут', 'offer'),
(2, '/photo/offers/psychologicalExamination.jpg', 'Психологическое обследование', 'Психологическое обследование (осмотр психолога) – это выявление индивидуально-личностных черт путём использования определенного набора тестов и практик.\r\n*Экспериментально-психологическое обследование состоит из нескольких этапов:\r\n*- изучение проблем пациента;\r\n*- проведение психологического эксперимента;\r\n*- обработка отдельных экспериментальных данных;\r\n*- анализ экспериментальных денных;\r\n*- составление заключения;\r\n*Составление «психологического портрета» помогает специалисту лучше «увидеть» своего клиента (а Вам – самого себя), что существенно облегчает терапию.', '1600', '60  минут', 'offer'),
(3, '/photo/offers/homeVisit.jpg', 'Выезд на дом', 'Выезд на дом – осуществляется, как правило, к пожилым людям, имеющим сложности в передвижении на дальние расстояния. А также – к пациентам с различными видами фобий, паническими расстройствами, шизофренией и другими состояниями, не позволяющими человеку покинуть дом.', '1600', 'от 30 до 60 минут', 'offer'),
(4, '/photo/offers/online.jpg', 'Онлайн консультирование', 'Онлайн-консультирование (дистанционное общение) – оказание психологической помощи в тех случаях, когда очное консультирование недоступно. Осуществляется посредством интернет-сети, аудио- и видео-чатов (скайп … , вайбер… ).', '1600', 'от 30 до 60 минут', 'offer');

